import React, { Component } from "react";
import './App.css';
import SplitterLayout from 'react-splitter-layout';
import 'react-splitter-layout/lib/index.css';
import Panel from 'muicss/lib/react/panel';
import DynamicForm from './components/dynamicForm/dynamicForm';
import myJson from './datatypes.json';
import txt from './datatypes.txt';

import json from './config/simple.json'

class App extends Component {

  submitHandler = (event) => {
    let json = JSON.stringify(event, null, 4);
    console.log(json);
    this.generateForm();
  }


  generateForm = () => {
    fetch(txt)
      .then((r) => r.text())
      .then(text => {
        let myarray = text.split('\n');
        /*
        var string = "text to split";
        string = string.split(" ");
        var stringArray = new Array();
        for (var i = 0; i < string.length; i++) {
          stringArray.push(string[i]);
          if (i != string.length - 1) {
            stringArray.push(" ");
          }
        }*/

        myarray.map((element, i) => {

          var array = element.split(' ');
            //console.log(array);

            if(array.length === 4){

              let name = array[0];
              name = name.replace('_',' ');
              let type = array[1];
              let options = array[2];
              options.replace(')','');
              options.replace('(','');
              let optionsArray = options.split(',');

              console.log(name,type,optionsArray);


            }
            if(array.length === 3){
              let name = array[0];
              name = name.replace('_',' ');
              let type = array[1];

              console.log(name,type);
            }

          })
        //var object = this.refs.DynamicForm;
        //var formObject = JSON.parse(json);
        //console.log(formObject);







      })
  }

  render() {
    return (
      <div className="App">

        <SplitterLayout>
          <div>Build a Query !
            <DynamicForm id="form" ref="form" config={json} onSubmit={(event) => this.submitHandler(event)}></DynamicForm>
          </div>
          <div>
            <Panel>
              {"sdknsdjsk"}
            </Panel>
          </div>
        </SplitterLayout>

      </div>
    );
  }
}

export default App;
