import React, { Component } from "react";
import './App.css';
import SplitterLayout from 'react-splitter-layout';
import 'react-splitter-layout/lib/index.css';
import Panel from 'muicss/lib/react/panel';
import DynamicForm from './components/dynamicForm/dynamicForm';
import myJson from './datatypes.json';
import txt from './datatypes.txt';
import json from './config/simple.json'

var formJson = {
  "name": "Query Form",
  "fields": []
};

class App extends Component {


  submitHandler = (event) => {
    let json = JSON.stringify(event, null, 4);
    console.log(json);
    this.generateForm();
  }


  generateForm = () => {
    fetch(txt)
      .then((r) => r.text())
      .then(text => {
        let myarray = text.split('\n');

        myarray.map((element, i) => {

          var array = element.split(' ');

          if (array.length === 4) {

            let name = array[0];
            name = name.replace(/_/g, ' ');
            let type = array[1];
            let options = array[2];
            options = options.replace(/\)/g, '');
            options = options.replace(/\(/g, '');
            let optionsArray = options.split(',');

            var optionData = {};

            optionsArray.map((option, i) => {
              optionData.push({ "display": option });
            })

            var data = {
              "id": name,
              "label": name,
              "description": "",
              "type": type,
              "value": "",
              "required": "false",
              "placeholder": "",
              "definition": {
                "options": [optionData]
              }
            };

          }
          else if (array.length === 3) {
            let name = array[0];
            name = name.replace(/_/g, ' ');
            let type = array[1];

            var data = {
              "id": name,
              "label": name,
              "description": "",
              "type": type,
              "value": "",
              "required": "false",
              "placeholder": ""
            };
          }




        })



      })
  }




  render() {
    return (
      <div className="App">

        <SplitterLayout>
          <div>Build a Query !
            <DynamicForm id="form" ref="form" config={json} onSubmit={(event) => this.submitHandler(event)}></DynamicForm>
          </div>
          <div>
            <Panel>
              {"sdknsdjsk"}
            </Panel>
          </div>
        </SplitterLayout>

      </div>
    );
  }
}

export default App;
